mapper.update:
UPDATE post
SET category = #{category}, title = #{title}, content = #{content}
WHERE pid = #{pid}

mapper.select:
SELECT p.pid, p.title, p.likes, p.views, p.created_date, p.cmtCnt, u.name userName, c.name categoryName
FROM (
SELECT * FROM post
USE INDEX(idx_post)
WHERE bid = #{bid}
ORDER BY pid DESC
LIMIT #{startList}, #{pageSize}
) p INNER JOIN user u ON p.uid = u.uid
INNER JOIN category_post c ON p.category = c.id
ORDER BY pid DESC

SELECT p.*, u.name userName, c.name categoryName
FROM (
SELECT * FROM post
USE INDEX(idx_post)
WHERE bid = #{bid}
ORDER BY pid DESC
LIMIT #{startList}, #{pageSize}
) p INNER JOIN user u ON p.uid = u.uid
INNER JOIN category_post c ON p.category = c.id
ORDER BY pid DESC

SELECT p.pid, p.title, p.likes, p.views, p.created_date, p.cmtCnt, u.name userName, c.name categoryName
FROM (
SELECT * FROM post
WHERE bid = #{bid} and category = #{category}
ORDER BY pid DESC
LIMIT #{startList}, #{pageSize}
) p INNER JOIN user u ON p.uid = u.uid
INNER JOIN category_post c ON p.category = c.id
ORDER BY pid DESC

SELECT pid FROM post
WHERE bid = ${bid}
ORDER BY likes DESC LIMIT 10

SELECT pid FROM post
WHERE bid = ${bid}
ORDER BY views DESC LIMIT 10

SELECT COUNT(*) as listCnt
FROM post
WHERE post.bid = #{bid}

SELECT COUNT(*) as listCnt
FROM post
WHERE post.bid = #{bid} and post.category = #{category}

mapper.update:
UPDATE post
SET cmtCnt = cmtCnt + 1
WHERE pid = #{pid}

UPDATE post
SET cmtCnt = cmtCnt - 1
WHERE pid = #{pid}

UPDATE post
SET views = views + 1
WHERE pid = #{pid}

UPDATE post
SET likes = likes + 1
WHERE pid = #{pid}
